mapper.select:
	
    select bno, title, content, writer, regdate, moddate, readcount from(
           select /*+INDEX_DESC(board pk_board) */ rownum  as rn, bno, title, content, writer, regdate, moddate, readcount
           from board where rownum <= #{pageNum} * #{amount}) 
    where rn > (#{pageNum} -1) * #{amount}
